import React from 'react';
import styled from 'styled-components';

const CardContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 100vh;
  background-color: #ffd6e7;
`;

const PhotosContainer = styled.div`
  display: flex;
  justify-content: space-around;
  width: 100%;
  margin: 20px 0;
`;

const Photo = styled.img`
  width: 200px;
  height: 200px;
  border-radius: 50%;
  object-fit: cover;
  border: 5px solid #ff69b4;
`;

const CakeGif = styled.img`
  width: 300px;
  margin: 20px 0;
`;

const WishText = styled.h1`
  font-size: 36px;
  color: #ff1493;
  text-align: center;
  margin: 20px 0;
`;

const BirthdayCard = () => {
  return (
    <CardContainer>
      <img src="/assets/firework.gif" alt="fireworks" style={{ width: '100%' }} />
      <PhotosContainer>
        <Photo src="/assets/friend1.jpg" alt="Friend 1" />
        <CakeGif src="/assets/happy-birthday-cake.gif" alt="Birthday Cake" />
        <Photo src="/assets/friend2.jpg" alt="Friend 2" />
      </PhotosContainer>
      <WishText>Happy Birthday Jain Sakhi ❤️</WishText>
    </CardContainer>
  );
};

export default BirthdayCard;
