import React, { useState, useEffect } from 'react';
import Heart from './components/Heart';
import BirthdayCard from './components/BirthdayCard';
import './App.css';

function App() {
  const [showCard, setShowCard] = useState(false);
  const [audio] = useState(new Audio('/assets/birthday-song.mp3'));

  const handleClick = () => {
    setShowCard(true);
    audio.play();
  };

  useEffect(() => {
    return () => {
      audio.pause();
      audio.currentTime = 0;
    };
  }, [audio]);

  return (
    <div className="App">
      {!showCard ? (
        <Heart onClick={handleClick} />
      ) : (
        <BirthdayCard />
      )}
    </div>
  );
}

export default App;
