import React, { useState, useEffect } from 'react';
import Heart from './components/Heart';
import BirthdayCard from './components/BirthdayCard';
import './App.css';

function App() {
  const [showCard, setShowCard] = useState(false);
  const [audio, setAudio] = useState(null);

  useEffect(() => {
    // Initialize audio when component mounts
    const birthdayAudio = new Audio('/assets/birthday-song.mp3');
    birthdayAudio.addEventListener('error', (e) => {
      console.error('Error loading audio:', e);
    });
    setAudio(birthdayAudio);

    // Cleanup when component unmounts
    return () => {
      if (birthdayAudio) {
        birthdayAudio.pause();
        birthdayAudio.currentTime = 0;
      }
    };
  }, []);

  const handleClick = () => {
    setShowCard(true);
    if (audio) {
      audio.play().catch(error => {
        console.error("Error playing audio:", error);
        // Continue showing the card even if audio fails
        setShowCard(true);
      });
    } else {
      // If audio isn't loaded, still show the card
      setShowCard(true);
    }
  };

  return (
    <div className="App">
      {!showCard ? (
        <Heart onClick={handleClick} />
      ) : (
        <BirthdayCard />
      )}
    </div>
  );
}

export default App;
